import React, { useState } from 'react';
import { useForm, Controller } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
const schema = yup.object().shape({
  exampleText: yup.string().required('This field is required'),
});

const DynamicForm = () => {
  const { control, handleSubmit } = useForm({
    resolver: yupResolver(schema),
  });

  const [fields, setFields] = useState([]);

  const addField = (type) => {
    if (type) {
      setFields((prevFields) => [...prevFields, { id: Date.now(), type }]);
    }
  };

  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <div>
      <select onChange={(e) => addField(e.target.value)} defaultValue="">
        <option value="" disabled>Select Field Type</option>
        <option value="text">Text Field</option>
        <option value="checkbox">Checkbox</option>
        <option value="radio">Radio Button</option>
        <option value="file">File Upload</option>
        {/* Add more field types as needed */}
      </select>

      <form onSubmit={handleSubmit(onSubmit)}>
        {fields.map((field) => (
          <div key={field.id}>
            <Controller
              control={control}
              name={`field-${field.id}`} // Unique name for each field
              render={({ field: { ref, ...rest } }) => {
                switch (field.type) {
                  case 'text':
                    return <input {...rest} placeholder="Text Field" ref={ref} />;
                  case 'checkbox':
                    return <input type="checkbox" {...rest} ref={ref} />;
                  case 'radio':
                    return <input type="radio" {...rest} ref={ref} />;
                  case 'file':
                    return <input type="file" {...rest} ref={ref} />;
                  default:
                    return null;
                }
              }}
            />
          </div>
        ))}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default DynamicForm;
